import java.math.BigInteger;import java.util.Scanner;public class Main {	public static void main(String[] args) {		Scanner input = new Scanner(System.in);		int b, k, odd;		odd = 0;		b = input.nextInt();		k = input.nextInt();		if (b % 2 == 0) {			for (int i = 1; i < k; i++) {				input.nextInt();			}			System.out.println(input.nextInt() % 2 == 0 ? "even" : "odd");		} else {			for (int i = 0; i < k; i++) if (input.nextInt() % 2 != 0) odd++;			System.out.println(odd % 2 == 0 ? "even" : "odd");		}	}}
