import java.util.*;import java.math.*;public class SuperheroTransformation{	public static void main(String[] args)		{			Scanner sc = new Scanner(System.in);			char[] arr1 = sc.next().toCharArray();			char[] arr2 = sc.next().toCharArray();			if(arr1.length!=arr2.length)			{				System.out.println("NO");				return;			}			ArrayList<Character> v = new ArrayList<>();			v.add('a');			v.add('e');			v.add('o');			v.add('i');			v.add('u');			for(int i = 0;i<arr1.length;i++)			{				boolean vowel = false;				if(v.contains(arr1[i]))					vowel =true;				if(v.contains(arr2[i])!=vowel)				{					System.out.println("NO");					return;				}			}			System.out.println("YES");		}}
