import java.util.*;public class Main{	public static void main(String[] args)	{		int x,y,z,a,b,c;		boolean flag=false;		Scanner reader=new Scanner(System.in);		x=reader.nextInt();		y=reader.nextInt();		z=reader.nextInt();		a=reader.nextInt();		b=reader.nextInt();		c=reader.nextInt();		if(a-x<0||a+b-x<y)			flag=true;		int t=a+b-x-y;		if(t+c<z)			flag=true;		if(flag)			System.out.println("NO");		else			System.out.println("YES");	}}		 		 		  		 		    			  		 				
