//package codechef;Â import java.util.*;import java.io.*;public class CodeChef{    public static int gcd(int a,int b)    {        if(b==0) return a;        return gcd(b,a%b);    }    public static int fact(int n)    {        int fact = 1;        for(int i = 1; i<=n; i++) fact*=i;        return fact;    }    public static boolean isPrime(int n)    {        if(n==2) return true;        else if(n%2==0) return false;        for(int i = 2; i<=Math.sqrt(n); i++)        {            if(n%i==0)            {                return false;            }        }        return true;    }    public static void main(String[] args)throws IOException    {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        String s1[] = br.readLine().split(" ");        String s2[] = br.readLine().split(" ");        int a = Integer.parseInt(s1[0]),b = Integer.parseInt(s1[1]),c = Integer.parseInt(s1[2]);        int green = Integer.parseInt(s2[0]),purple = Integer.parseInt(s2[1]),black = Integer.parseInt(s2[2]);        if(green<a||purple+green-a<b||black+purple+green-a-b<c) System.out.println("NO");        else System.out.println("YES");    }}
