//stan hu taoimport static java.lang.Math.max;import static java.lang.Math.min;import static java.lang.Math.abs;import java.util.*;import java.io.*;import java.math.*;Â public class x1117A{    public static void main(String hi[]) throws Exception    {        BufferedReader infile = new BufferedReader(new InputStreamReader(System.in));        StringTokenizer st = new StringTokenizer(infile.readLine());        int N = Integer.parseInt(st.nextToken());        int[] arr = readArr(N, infile, st);        int res = 0, t = 0;        int tag = arr[0];        int size = 1;        for(int i=1; i < N; i++)        {            if(arr[i] != tag)            {                if(tag > t)                {                    res = size;                    t = tag;                }                else if(tag == t)                    res = max(res, size);                size = 0;                tag = arr[i];            }            size++;        }        if(tag > t)        {            res = size;            t = tag;        }        else if(tag == t)            res = max(res, size);        System.out.println(res);    }    public static int[] readArr(int N, BufferedReader infile, StringTokenizer st) throws Exception    {        int[] arr = new int[N];        st = new StringTokenizer(infile.readLine());        for(int i=0; i < N; i++)            arr[i] = Integer.parseInt(st.nextToken());        return arr;    }}
