  import java.util.*;import java.io.*;public class B{    public static FastReader sc=new FastReader();        public static PrintWriter  out = new PrintWriter(System.out);         public static void taskSolver(){           int n=sc.nextInt();int ans=0;        int arr[]=new int[n+1];int oddsuff=0;int evensuff=0;        for(int i=1;i<=n;i++){            arr[i]=sc.nextInt();            if(i%2==0)evensuff+=arr[i];            else oddsuff+=arr[i];        }int oddpref=0,evenpref=0;        for(int i=1;i<=n;i++){            if(i%2==0)evensuff-=arr[i];            else oddsuff-=arr[i];            if(oddpref+evensuff==evenpref+oddsuff)ans++;            if(i%2==0)evenpref+=arr[i];            else oddpref+=arr[i];        }          out.println(ans);    }          public static void main(String args[]) throws java.lang.Exception{                        taskSolver();           out.close();        }           static  class FastReader     {         BufferedReader br;         StringTokenizer st;           public FastReader()         {             br = new BufferedReader(new                     InputStreamReader(System.in));         }         String next()         {             while (st == null || !st.hasMoreElements())             {                 try                {                     st = new StringTokenizer(br.readLine());                 }                 catch (IOException  e)                 {                     e.printStackTrace();                 }             }             return st.nextToken();         }         int nextInt()         {             return Integer.parseInt(next());         }           long nextLong()         {             return Long.parseLong(next());         }           double nextDouble()         {             return Double.parseDouble(next());         }         String nextLine()         {             String str = "";             try            {                 str = br.readLine();             }             catch (IOException e)             {                 e.printStackTrace();             }             return str;         }     }      } 
