// discussed with rainboyimport java.io.*;import java.util.*;Â public class CF1120B {	static final int C = 100000;	static char[] aa, bb;	static int[] dd, ss;	static int c; 	static void inc(int i, int n) {		if (c == C)			return;		if (aa[i + 1] == '9') {			dec(i + 1, n);			if (c == C)				return;		}		aa[i]++; aa[i + 1]++;		dd[c] = i + 1;		ss[c] = 1;		c++;	}	static void dec(int i, int n) {		if (c == C)			return;		if (aa[i + 1] == '0') {			inc(i + 1, n);			if (c == C)				return;		}		aa[i]--; aa[i + 1]--;		dd[c] = i + 1;		ss[c] = -1;		c++;	}	public static void main(String[] args) throws IOException {		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));		int n = Integer.parseInt(br.readLine());		aa = br.readLine().toCharArray();		bb = br.readLine().toCharArray();		long sum = 0;		int d = 0;		for (int i = 0; i < n; i++) {			d = bb[i] - aa[i] - d;			sum += Math.abs(d);		}		if (d != 0) {			System.out.println(-1);			return;		}		dd = new int[C];		ss = new int[C];		for (int i = 0; i < n; i++)			while (c < C && aa[i] != bb[i])				if (aa[i] < bb[i])					inc(i, n);				else					dec(i, n);		PrintWriter pw = new PrintWriter(System.out);		pw.println(sum);		for (int i = 0; i < c; i++)			pw.println(dd[i] + " " + ss[i]);		pw.close();	}}
