import java.util.Scanner;import java.util.Arrays;/* Name of the class has to be "Main" only if the class is public. */public class Main{	public static void main (String[] args) throws java.lang.Exception	{		try {		    Scanner sc = new Scanner(System.in);		    int t,n,m,i,j,k,count,min,sch,pow;		    t=1;		    while(t>0)		    {		        t--;		        n=sc.nextInt();		        m=sc.nextInt();		        k=sc.nextInt();		        int p[] =new int[n];		        int s[] =new int[n];		        int c[] =new int[k];		        for(i=0;i<n;i++)		         p[i]=sc.nextInt();		         for(i=0;i<n;i++)		         s[i]=sc.nextInt();		        for(i=0;i<k;i++)		         c[i]=sc.nextInt();		         count=0;		         for(i=0;i<k;i++)		         {		          min=c[i]-1;		          sch=s[min];		          pow=p[min];		          for(j=0;j<n;j++)		          {		              if(s[j]==sch && p[j]>pow)		              {		                  count++;		                  break;		              }		          }		         }		         System.out.println(count);		    }		} catch(Exception e) {		}	}}
