import java.util.*;public class Main{    public static void main(String args[])    {        Scanner cin=new Scanner(System.in);        int n;        n=cin.nextInt();        int num;        if(n%2==0)num=n/2;        else num=n/2+1;        int a[]=new int[n];        for(int i=0;i<n;i++)a[i]=cin.nextInt();        int num1=0,num2=0;        for(int i=0;i<n;i++)        {            if(a[i]>0)num1++;            else if(a[i]<0)num2++;        }        if(num1>=num2&&num1>=num)System.out.println(1);        else if(num1<num2&&num2>=num)System.out.println(-1);        else System.out.println(0);    }}
