//package com.tanf.codeforces; import java.util.Arrays;import java.util.HashMap;import java.util.HashSet;import java.util.Scanner; public class Main {    private static boolean isVowels(char c) {        if (c == 'a' ||c == 'o' ||c == 'y' ||c == 'e' ||c == 'u' ||c == 'i'){            return true;        }        return false;    }     public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        int n = scan.nextInt();        int[] shops = new int[n*2];        HashMap<Integer, Integer> mp =new HashMap<>();        HashMap<Integer, Integer> mp1 =new HashMap<>();        for (int i= 0; i < n*2; i++){            shops[i] = scan.nextInt();            if(mp.getOrDefault(shops[i], -1) == -1) {                mp.put(shops[i], i);            } else {                mp1.put(shops[i], i);            }        }        long res = 0;        int loc1 = 0;        int loc2 =0;        for(int i=1;i <= n;i++) {            int pos = mp.getOrDefault(i, -1);            int pos1 = mp1.getOrDefault(i, -1);            int res1 = Math.abs(loc1-pos) + Math.abs(loc2-pos1);            int res2 = Math.abs(loc2-pos) + Math.abs(loc1-pos1);            loc1 = pos;            loc2 = pos1;            res += Math.min(res1, res2);        }          System.out.println(res);      }}
