import java.util.*;// import java.lang.*;import java.io.*;/*** @author zeel prajapati */// @SuppressWarnings("unchecked")// public class Main{public final class main{ public static void main(String[] args)throws IOException{ BufferedReader br=new BufferedReader(new InputStreamReader(System.in)); int n=Integer.parseInt(br.readLine()); int m=998244353; long arr[]=Inputarr(br);  int cout1=1,cout2=0,f=1,ans=-1,max=-1;  int i=0,j=1;  while(j<n){      if(arr[j]!=arr[j-1]){          if(f==0){              cout1=cout2;              cout2=0;            }else{                f--;            }        }        if(f==0){            cout2++;        }else{            cout1++;        }        ans=Math.min(cout1,cout2);        max=Math.max(ans,max);      j++;  }//   System.out.println(cout1+" "+cout2);//   ans=Math.min(cout1,cout2);  System.out.println(max*2);  //  System.out.println(ans); br.close(); System.exit(0);;} public static long[] Inputarr(BufferedReader br){    try{    String s=br.readLine();    String d[]=s.split(Character.toString(' '));    int n=d.length;    long arr[]=new long[n];    for(int i=0;i<n;i++){        arr[i]=Long.parseLong(d[i]);    }        return arr;    }catch(IOException e){     System.out.println(1);     }    return new long[1];} public static void printarr(int arr[]){    StringBuffer br=new StringBuffer();    for(int i=0;i<arr.length;i++){        br.append(arr[i]+Character.toString(' '));    }    System.out.println(br);}}
