import java.util.*;import java.io.*;public final class Codeforces{    public static BufferedReader buffer=new BufferedReader(                                            new InputStreamReader(System.in)                                        );    public static BufferedWriter logger=new BufferedWriter(                                            new OutputStreamWriter(System.out)                                        );    public static void main(String[] args){        try{            int n=Integer.parseInt(buffer.readLine());            String s=buffer.readLine();            long even=0;            for(int i=0;i<n;i++){                if(((int)s.charAt(i))%2==0){                    even+=(i+1);                }            }            logger.write(even+" ");            logger.flush();        }        catch(IOException exc){}    }}
