import java.util.Scanner;public class Solution{	public static void main(String args[]){		Scanner sc = new Scanner(System.in);		int n = sc.nextInt();		long l = sc.nextLong();		int z1=0;		int z2=0;		long po=n;		if(n==1){			if (l==1) {				System.out.println("YES");				System.out.println(0);			}else{				System.out.println("NO");			}		}else {			while (po <= l) {				if (po == l) {					System.out.println("YES");					System.out.println(z1);					z2++;					po = po * n;				} else {					po = po * n;					z1++;				}			}			if (z2 == 0) {				System.out.println("NO");			}		}	}}
