//stan hu tao//join nct ridin by first year culture repsimport static java.lang.Math.max;import static java.lang.Math.min;import static java.lang.Math.abs;import java.util.*;import java.io.*;import java.math.*;Â public class x1140B{    public static void main(String hi[]) throws Exception    {        BufferedReader infile = new BufferedReader(new InputStreamReader(System.in));        StringTokenizer st = new StringTokenizer(infile.readLine());        int T = Integer.parseInt(st.nextToken());        StringBuilder sb = new StringBuilder();        while(T-->0)        {            int N = Integer.parseInt(infile.readLine());            char[] arr = infile.readLine().toCharArray();            int res = 0;            for(int i=0; i < N; i++)            {                if(arr[i] != '<')                    break;                res++;            }            int temp = 0;            for(int i=N-1; i >= 0; i--)            {                if(arr[i] != '>')                    break;                temp++;            }            res = min(res, temp);            sb.append(res+"\n");        }        System.out.print(sb);    }}
