import java.util.*;public class Main {	public static void main(String[] args){		Scanner sc=new Scanner(System.in);		int n=sc.nextInt();		long sum=0;		ArrayList<Integer> odd=new ArrayList<Integer>();		ArrayList<Integer> even=new ArrayList<Integer>();		while(n-->0){			int t=sc.nextInt();			if(t%2==0)				even.add(t);			else				odd.add(t);		}		Collections.sort(odd);		Collections.sort(even);		int t = Math.abs(even.size()-odd.size());				if(t<=1)			sum=0;		else{			t--;			if(even.size()>odd.size()){				for(int i=0;i<t;i++){					sum+=even.get(i);				}			}else{				for(int i=0;i<t;i++){					sum+=odd.get(i);				}			}		}				System.out.println(sum);	}}
