// package com.tvv.amazing.algorithm.math; import java.util.Scanner; public class LoveA {     public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        System.out.println(GoodStringCook.of(scanner.nextLine()).cook());    }     public static class GoodStringCook {        public String s;         public GoodStringCook(String s) {            this.s = s;        }         public static GoodStringCook of(String s) {            return new GoodStringCook(s);        }         public long cook() {            long count = 0;            for (int i = 0; i < s.length(); ++i) {                if (s.charAt(i) == 'a') {                    count ++;                }            }             return count * 2 - 1 > s.length() ? s.length() : count * 2 - 1;        }    }}
