import java.util.*;import java.io.*; public class Main {	public static void main(String args[]) {new Main().work();}		Scanner in = new Scanner(System.in);	PrintWriter out = new PrintWriter(System.out);		void work() {				int n = in.nextInt(), m = in.nextInt();		        ArrayList<Integer>[] graph=(ArrayList<Integer>[])new ArrayList[n];        for(int i=0;i<n;i++) graph[i]=new ArrayList<Integer>();        for(int i=0;i<m;i++){            int c1=in.nextInt()-1;            int c2=in.nextInt()-1;                        if(c1<c2){                graph[c1].add(c2);                graph[c2].add(c1+n);            }else{                graph[c2].add(c1);                graph[c1].add(c2+n);            }        }            for(int i=0;i<n;i++) Collections.sort(graph[i]);        boolean f=false;        out:        for(int i=1;i<n;i++){            if(n%i!=0) continue;            for(int j=0;j<n;j++){                int k=(j+i)%n;                if(graph[j].size()!=graph[k].size()) continue out;                for(int l=0;l<graph[j].size();l++){                    if((graph[j].get(l)+i)%n!=graph[k].get(l)%n){                        continue out;                    }                  }            }            f=true;            break;        }                if(f){            out.print("Yes");        }else{            out.print("No");        }		out.flush();	}}
