//package testing;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.io.PrintWriter;import java.lang.reflect.Array;import java.util.*; public class Main {	static public class Pair{		long a,b,c;		public Pair(long a,long b,long c) {			this.a = a;			this.b = b;			this.c = c;		}	}	static class cmp implements Comparator<Pair>{		public int compare(Pair p,Pair q) {			if(p.a*p.a*p.b<q.a*q.b*q.a) {				return -1;			}			return 1;		}	}	public static void main(String[] args) {		FastScanner fs = new FastScanner();		PrintWriter out = new PrintWriter(System.out);		int test = 1;//		test = fs.nextInt();		outer:		while(test-->0) {		  long a,b,c;		  a = fs.nextInt();		  b = fs.nextInt();		  c = fs.nextInt();		  long ans = 2*c;		  long back = Math.min(a, b);		  a-=back;		  b-=back;		  ans+=(back*2);	  		  if(a>=1||b>=1) {			  ans+=1;		  }		  out.println(ans);		}		out.close();	} 	static class FastScanner {		BufferedReader br=new BufferedReader(new InputStreamReader(System.in));		StringTokenizer st=new StringTokenizer(""); 		String next() {			while (!st.hasMoreTokens())				try {					st=new StringTokenizer(br.readLine());				} catch (IOException e) {					e.printStackTrace();				}			return st.nextToken();		} 		int nextInt() {			return Integer.parseInt(next());		} 		int[] readArray(int n) {			int[] a=new int[n];			for (int i=0; i<n; i++)				a[i]=nextInt();			return a;		} 		long nextLong() {			return Long.parseLong(next());		}	} }
