//package albert.codeforces.bornthisway1148b; import java.util.Scanner; public class Solution {    public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        int n = scan.nextInt();        int m = scan.nextInt();        int ta = scan.nextInt();        int tb = scan.nextInt();        int k = scan.nextInt();        int[] a = new int[n];        int[] b = new int[m];        for (int i = 0; i < n; i++) {            a[i] = scan.nextInt();        }        for (int i = 0; i < m; i++) {            b[i] = scan.nextInt();        }        if (k >= n || k >= m) {            System.out.println(-1);            return;        }         int r = 0;        int ans = 0;        for (int i = 0; i <= k; i++) {            int rest = k - i;            int arrInB = a[i] + ta;            while (r < m && b[r] < arrInB) {                r++;            }            if (m - r <= rest) {                System.out.println(-1);                return;            }            ans = Math.max(ans, b[r + rest] + tb);        }        System.out.println(ans);    }}
