import java.util.*;public class sol{static ArrayList<Integer> []adj = new ArrayList[10000];	static int ans= 0;		static int max(int x,int y) {		return x>=y?x:y;	}	static void dfs(int u,int depth) {		for(int v:adj[u]) {			dfs(v,depth+1);		}		ans = max(ans,depth);	}		public static void main(String[] args) {		Scanner input = new Scanner(System.in);				int n=input.nextInt();		for(int i=1;i<=n;i++) {			adj[i]= new ArrayList<Integer>();			int x =input.nextInt();			if(x!=-1) {				adj[i].add(x);			}						}		for(int i=1;i<=n;i++) {			dfs(i,1);		}				System.out.println(ans);	}}
