# Question: I - 2# Assignment 6# Daniel Perez, bd2255  def rainy(MAP, S):    for r in range(S):        for c in range(S):            if MAP[r][c] == '.':                if r - 1 >= 0 and r + 1 < S and c - 1 >= 0 and c + 1 < S:                    topC = MAP[r-1][c]                    botC = MAP[r+1][c]                    lefC = MAP[r][c-1]                    rigC = MAP[r][c+1]                     if topC == botC and botC == lefC and lefC == rigC and rigC == '.':                        MAP[r-1][c] = '*'                        MAP[r+1][c] = '*'                        MAP[r][c-1] = '*'                        MAP[r][c+1] = '*'                        MAP[r][c] = '*'    for r in range(S):        for c in range(S):            if MAP[r][c] == '.':                return False     return True            S = int(input())MASTER = []for i in range(S):    X = input()    X = [c for c in X]    MASTER.append(X) if rainy(MASTER, S):    print('YES')else:    print('NO')      			 	  	 	   	   		 	 	 			
