import java.util.*;public class Main{	public static void main(String[] args)	{		int a,b,ji1=0,ji2=0,ou1=0,ou2=0;		Scanner reader=new Scanner(System.in);		a=reader.nextInt();		b=reader.nextInt();		int i,x;		for(i=0;i<a;++i)		{			x=reader.nextInt();			if(x%2==1)				ji1++;			else				ou1++;		}		for(i=0;i<b;++i)		{			x=reader.nextInt();			if(x%2==1)				ji2++;			else				ou2++;		}		if(ji1>ou2)			ji1=ou2;		if(ji2>ou1)			ji2=ou1;		System.out.println(ji1+ji2);	}} 		 		 		 		    	  	  						 		
