import java.util.*;public class Solution{    public static void main(String args[]){        Scanner sc=new Scanner(System.in);        int a,b,c,d;        a=sc.nextInt();        b=sc.nextInt();        c=sc.nextInt();        d=sc.nextInt();        int x=Math.max(a,Math.max(b,Math.max(c,d)));        if(d!=x)            System.out.print(x-d+" ");        if(c!=x)        System.out.print(x-c+" ");        if(b!=x)        System.out.print(x-b+" ");        if(a!=x)        System.out.print(x-a+" ");    }}
