import java.util.*;import java.lang.*;public class Main{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);        String str = sc.nextLine();        int c = str.length();        int x = Integer.parseInt(str.substring(0, str.indexOf(" ")));        int y = Integer.parseInt(str.substring(str.indexOf(" ")+1, str.indexOf(" ",str.indexOf(" ")+1)));        int z = Integer.parseInt(str.substring(str.indexOf(" ", str.indexOf(" ")+2)+1, str.lastIndexOf(" ")));        int t = Integer.parseInt(str.substring(str.lastIndexOf(" ")+1, c));        int a = Math.max(Math.max(x,y),Math.max(z,t));        if (t==a) {        System.out.println((a-x)+" "+ (a-y) +" "+(a-z));        }        if (x==a) {        System.out.println((a-y) +" "+(a-z)+" "+ (a-t));        }        if (y==a) {        System.out.println((a-x)+" "+(a-z)+" "+ (a-t));        }        if (z==a) {        System.out.println((a-x)+" "+ (a-y) +" "+ (a-t));        }    }}
