import java.util.*;public class telephone{    public static void main(String[] args){        Scanner sc= new Scanner(System.in);        int n= sc.nextInt();        String s= sc.next();        int chancePetya=(n-11)/2;        int count8=0; int b=0;        for(int i=0; i<=n-1; i++){            if(s.charAt(i)=='8')count8++;            else b++;            if(count8>chancePetya)break;        }        if(count8<=chancePetya)System.out.println("NO");        else if(b<=chancePetya)System.out.println("YES");        else System.out.println("NO");    }}
