import java.io.*;import java.util.*;public class Main {    public static void main(String args[])    {        FastReader input=new FastReader();        PrintWriter out=new PrintWriter(System.out);        int T=input.nextInt();        while(T-->0)        {            String s=input.next();            int a[]=new int[26];            for(int i=0;i<s.length();i++)            {                a[s.charAt(i)-97]++;            }            ArrayList<Integer> list1=new ArrayList<>();            ArrayList<Integer> list2=new ArrayList<>();            int f=0;            int x=0;            for(int i=0;i<26;i++)            {                if(a[i]!=0)                {                    x++;                    if(f==0)                    {                        list1.add(i);                        f=1;                    }                    else                    {                        list2.add(i);                        f=0;                    }                }            }            list2.addAll(list1);            int flag=0;            for(int i=0;i<list2.size()-1;i++)            {                int d=Math.abs(list2.get(i)-list2.get(i+1));                if(d==1)                {                    flag=1;                    break;                }            }            if(flag==1)            {                if(list2.size()==3)                {                    int v=list2.get(0);                    list2.remove(0);                    list2.add(v);                    int fl=0;                    for(int i=0;i<list2.size()-1;i++)                    {                        int d=Math.abs(list2.get(i)-list2.get(i+1));                        if(d==1)                        {                            fl=1;                            break;                        }                    }                    if(fl==1)                    {                        out.println("No answer");                    }                    else                    {                        for(int i=0;i<list2.size();i++)                        {                            int val=list2.get(i);                            int r=a[val];                            char ch=(char)(val+97);                            for(int j=0;j<r;j++)                            {                                out.print(ch);                            }                        }                        out.println();                    }                }                else                {                    out.println("No answer");                }            }            else            {                for(int i=0;i<list2.size();i++)                {                    int v=list2.get(i);                    int r=a[v];                    char ch=(char)(v+97);                    for(int j=0;j<r;j++)                    {                        out.print(ch);                    }                }                out.println();            }        }        out.close();    }    static class FastReader    {        BufferedReader br;        StringTokenizer st;        public FastReader()        {            br = new BufferedReader(new InputStreamReader(System.in));        }        String next()        {            while (st == null || !st.hasMoreElements())            {                try                {                    st = new StringTokenizer(br.readLine());                }                catch (IOException e)                {                    e.printStackTrace();                }            }            return st.nextToken();        }        int nextInt()        {            return Integer.parseInt(next());        }        long nextLong()        {            return Long.parseLong(next());        }        double nextDouble()        {            return Double.parseDouble(next());        }        String nextLine()        {            String str = "";            try            {                str = br.readLine();            }            catch (IOException e)            {                e.printStackTrace();            }            return str;        }    }}
