import java.util.ArrayList;import java.util.Arrays;import java.util.Collections;import java.util.Scanner;import java.lang.Math; public class Account {			public static void main (String[] args) {				Scanner sc= new Scanner(System.in) ;		int n= sc.nextInt() ;		int m= sc.nextInt() ;		sc.nextLine() ;		char[][] mat= new char[n][n] ;		for(int i=0 ;i<n ;i++ ) {			mat[i]= (sc.nextLine()).toCharArray() ;		}		int count= 0 ;		for(int i=0 ;i<n ;i++ ) {			for(int j=0 ;j<m ;j++ ) {				if(mat[i][j] == 'P') {					if(i>0) {						if(mat[i-1][j] == 'W') {							count++ ;							mat[i-1][j]= '.' ;							continue ;						}					}if(i < n-1) {						if(mat[i+1][j] == 'W') {							count++ ;							mat[i+1][j] = '.' ;							continue ;						}					}if(j>0) {						if(mat[i][j-1] == 'W') {							count++ ;							mat[i][j-1] = '.' ;							continue ;						}					}if(j < m-1) {						if(mat[i][j+1] == 'W') {							count++ ;							mat[i][j+1] = '.' ;							continue ;						}					}				}			}		}		System.out.println(count);	}		}
