# MasterKali from sys import stdinfrom collections import Counterfrom math import sqrt, factorial, log10, log, floor, ceilfrom itertools import permutations, combinations, combinations_with_replacement input = stdin.readlinedef li(): return list(map(int, input().split()))def lis(): return list(map(str, input().split()))def mp(): return map(int, input().split())def inp(): return int(input())def inps(): return str(input().strip())def pr(n): return stdout.write(str(n)+"\n") INF = float('inf')  def solve():	n, h, m = mp()	cnt = [h]*n	for i in range(m):		x, y, z = mp()		for i in range(x, y+1):			cnt[i-1] = min(cnt[i-1], z) 	res = 0	for i in range(n):		res += cnt[i]*cnt[i]	print(res) t = 1for i in range(t):		solve() 
