import java.util.*;import java.util.Arrays;public class Main{    public static void main (String[] args)      {          Scanner sc=new Scanner(System.in);        int n=sc.nextInt();        int a=sc.nextInt();        int x=sc.nextInt();        int b=sc.nextInt();        int y=sc.nextInt();        a--;b--;x--;y--;        while(true)        {            if(a==b)            {                System.out.println("YES");                break;            }            if(a==x || b==y)            {                System.out.println("NO");                break;            }            a=(a+1)%n;            b=(b-1+n)%n;        }    }  } 
