import java.util.*;public class Solution{    public static void main(String[] args){        Scanner s=new Scanner(System.in);        int n=s.nextInt();        int a=s.nextInt();        int x=s.nextInt();        int b=s.nextInt();        int y=s.nextInt();        int found=0;        for(int i=1;i<=n;i++){                                    a=a+1;            if(a==n+1)                  a=a%n;            b=b-1;            if(b==0)                b=n;                        if(a==x || b==y){                boolean v=check(a,b);                if(v==true)                    found=1;                break;            }            boolean v1=check(a,b);            if(v1==true){                found=1;                break;            }                        }        if(found==1)            System.out.println("Yes");        else            System.out.println("No");            }    public static boolean check(int a,int b){        boolean c=false;        if(a==b)            c=true;        return c;    }}
