import java.io.*;import java.util.*;public class Main {    public static void main(String args[])    {        FastReader input=new FastReader();        PrintWriter out=new PrintWriter(System.out);        int T=1;        while(T-->0)        {            int n=input.nextInt();            int m=input.nextInt();            int a[][]=new int[m][2];            for(int i=0;i<m;i++)            {                a[i][0]=input.nextInt();                a[i][1]=input.nextInt();            }            int x=a[0][0],y=a[0][1];            int index=-1;            for(int i=1;i<m;i++)            {                if(a[i][0]!=x && a[i][1]!=x && a[i][0]!=y && a[i][1]!=y)                {                    index=i;                    break;                }            }            if(index==-1)            {                out.println("YES");            }            else            {                int flag=0;                for(int i=0;i<2;i++)                {                    for(int j=0;j<2;j++)                    {                        x=a[0][i];                        y=a[index][j];                        int f=0;                        for(int k=0;k<m;k++)                        {                            if(!(a[k][0]==x || a[k][1]==x || a[k][0]==y || a[k][1]==y))                            {                                f=1;                                break;                            }                        }                        if(f==0)                        {                            flag=1;                            break;                        }                    }                }                if(flag==1)                {                    out.println("YES");                }                else                {                    out.println("NO");                }            }        }        out.close();    }    static class FastReader    {        BufferedReader br;        StringTokenizer st;        public FastReader()        {            br = new BufferedReader(new InputStreamReader(System.in));        }        String next()        {            while (st == null || !st.hasMoreElements())            {                try                {                    st = new StringTokenizer(br.readLine());                }                catch (IOException e)                {                    e.printStackTrace();                }            }            return st.nextToken();        }        int nextInt()        {            return Integer.parseInt(next());        }        long nextLong()        {            return Long.parseLong(next());        }        double nextDouble()        {            return Double.parseDouble(next());        }        String nextLine()        {            String str = "";            try            {                str = br.readLine();            }            catch (IOException e)            {                e.printStackTrace();            }            return str;        }    }}
