#include <bits/stdc++.h> using namespace std; const int mod = 998244353;const int NN = 202020; int deg[NN], fac[NN]; int main() {#ifndef ONLINE_JUDGE	freopen("in.in ","r ",stdin);	freopen("out.out ","w ",stdout);#endif	fac[0] = 1;	for(int i=1; i<NN; i++) fac[i] = 1LL * fac[i-1] * i % mod;	int n; cin>>n;	for(int i=1; i<n; i++) {		int u, v;		scanf("%d %d", &u, &v);		deg[u]++; deg[v]++;	}	int ans = 1;	for(int i=1; i<=n; i++) {		ans = 1LL * ans * fac[deg[i]] % mod;	}	ans = 1LL * ans * n % mod;	cout<<ans;		return 0;}             
