import java.util.*;public class MyClass {    public static void main(String args[]) {      Scanner sc=new Scanner(System.in);      int t=sc.nextInt();      while(t-->0)      {          int n=sc.nextInt();          int[] arr=new int[n];          int[] b=new int[3];          for(int i=0;i<n;i++)          {arr[i]=sc.nextInt();          b[arr[i]%3]++;          }          int ans=b[0];          int min=Math.min(b[1],b[2]);          ans=ans+min;          b[1]=b[1]-min;          b[2]=b[2]-min;          System.out.println(ans+(b[1]+b[2])/3);                          }    }}
