// 118B - Present from Lena// https://codeforces.com/problemset/problem/118/B import java.util.Scanner; public class CF118B {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        for (int row = -n; row <= n; ++row) {            int top = n - Math.abs(row);            for (int i = 0; i < Math.abs(row); ++i) {                System.out.print("  ");            }            for (int i = 0; i < top; ++i) {                System.out.print(i);                System.out.print(" ");            }            for (int i = top; i > 0; --i) {                System.out.print(i);                System.out.print(" ");            }            System.out.println(0);        }        sc.close();    }}
