import java.util.*;public class Main{  	public static void main(String[] args) {		Scanner sc=new Scanner(System.in);	    int N=sc.nextInt();	    for(int i=0;i<=N;i++){	        for(int j=0;j<2*(N-i);j++){	            System.out.print(" ");	        }	        for(int j=0;j<i+1;j++){	            System.out.print((j==i)?j:j+" ");	        }	        for(int j=i-1;j>=0;j--){	             System.out.print(" "+j);	        }	        System.out.println();	    }	    for(int i=0;i<N;i++){	        for(int j=0;j<2*(i+1);j++){	            System.out.print(" ");	        }	        for(int j=0;j<N-i;j++){	            System.out.print((j==N-i-1)?j:j+" ");	        }	        for(int j=N-i-2;j>=0;j--){	             System.out.print(" "+j);	        }	        System.out.println();	    }	}}
