import java.util.*;import java.util.Arrays;public class Main{    public static void main (String[] args)      {          Scanner sc=new Scanner(System.in);        int[] a=new int[4];        for(int i=1;i<=3;i++)        {            a[i]=sc.nextInt();        }        Arrays.sort(a);        int d= sc.nextInt();        int i=1,total=0;        if(a[i+1]-a[i]<d)        {            total=total+(d-(a[i+1]-a[i]));                    }        i++;        if(a[i+1]-a[i]<d)        {            total=total+(d-(a[i+1]-a[i]));                    }        System.out.println(total);    }  }         
