// practice with rainboyimport java.io.*;import java.util.*; public class CF1188B extends PrintWriter {	CF1188B() { super(System.out, true); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF1188B o = new CF1188B(); o.main(); o.flush();	} 	void main() {		int n = sc.nextInt();		int p = sc.nextInt();		int k = sc.nextInt();		int[] aa = new int[n];		for (int i = 0; i < n; i++) {			int a = sc.nextInt();			int b = (int) (((long) a * a % p * a % p * a - (long) a * k) % p);			if (b < 0)				b += p;			aa[i] = b;		}		aa = Arrays.stream(aa).boxed().sorted().mapToInt($->$).toArray();		long ans = 0;		for (int i = 0, j; i < n; i = j) {			j = i + 1;			while (j < n && aa[j] == aa[i])				j++;			ans += (long) (j - i) * (j - i - 1) / 2;		}		println(ans);	}}
