import java.util.*;import java.util.Arrays;public class Main{    public static void main(String args[])    {        Scanner sc=new Scanner(System.in);        int n=sc.nextInt();        String s=sc.next();        char[] a=new char[n];        int count=0,count1=0;        for(int i=0;i<n;i++)        {            a[i]=s.charAt(i);            if(a[i]=='0')            {                count++;            }            else            {                count1++;            }        }        if(count==count1)        {            System.out.println("2 ");            for(int i=0;i<n-1;i++)            {                System.out.print(a[i]);            }            System.out.print(" ");            System.out.print(a[n-1]);                    }        else if(n==1)        {            int j=0;            System.out.println("1 ");            System.out.print(a[j]);        }        else        {            System.out.println("1 ");            for(int i=0;i<n;i++)            {                System.out.print(a[i]);            }                                }                    }}
