    import java.util.*;    public class Solution{            public static void main(String[] args){        Scanner s=new Scanner(System.in);        int n=s.nextInt();        s.nextLine();        String s1=s.nextLine();        String result="";        int count=0,count1=0,count2=0;          for(int j=0;j<n;j++){            if(s1.charAt(j)=='0')                count++;            else                count1++;                    }        if(count==count1){            int k=n/count;            System.out.println("2");            System.out.print(s1.charAt(0)+" ");            for(int j=1;j<n;j++){                                result=result+s1.charAt(j);            }                       System.out.print(result);        }        else{            System.out.println("1");            System.out.println(s1);        }                                                }      }    
