//package codechef;Â import java.util.*;import java.io.*;public class CodeChef{    public static int gcd(int a,int b)    {        if(b==0) return a;        return gcd(b,a%b);    }    public static void main(String[] args)throws IOException    {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        String s[] = br.readLine().split(" ");        int a = Integer.parseInt(s[0]), b = Integer.parseInt(s[1]), n = Integer.parseInt(s[2]);        while(true)        {            if(gcd(a,n)>n)            {                System.out.println("1");                break;            }            else n = n - gcd(a,n);            if(gcd(b,n)>n)            {                System.out.println("0");                break;            }            else n = n - gcd(b,n);        }    }}
