import java.util.*;public class asd{         static Scanner s=new Scanner(System.in);         public static void main(String args[])         {                  int t=s.nextInt();                  while(t-->0)                  {                           solve();                  }                           }         public static void solve()         {                  int m=s.nextInt();                  int n=s.nextInt();                  String str[]=new String[m];                  for(int i=0;i<m;i++)                  str[i]=s.next();                  int min1=Integer.MAX_VALUE;ArrayList<Integer> list1=new ArrayList<>();ArrayList<Integer> list2=new ArrayList<>();                  int min2=Integer.MAX_VALUE;                  for(int i=0;i<m;i++)                  {        int count=0;                           for(int j=0;j<str[i].length();j++)                           {                           char ch=str[i].charAt(j);                           if(ch=='.')                           count++;                           }                           if(min1==count)                               list1.add(i);                           if(min1>count)                           {                               list1.clear();list1.add(i);                           min1=Math.min(min1,count);                           }                  }                  for(int j=0;j<str[0].length();j++)                  {                            int count=0;                           for(int i=0;i<m;i++)                           {                                    char ch=str[i].charAt(j);                                    if(ch=='.')                           count++;                           }                           if(min2==count)                               list2.add(j);                           if(min2>count)                           {                               list2.clear();list2.add(j);                           min2=Math.min(min2,count);}                  }                  int MIN=min1+min2;                  for(int i=0;i<list1.size();i++)                  {                      for(int j=0;j<list2.size();j++ )                      {                          char ch=str[list1.get(i)].charAt(list2.get(j));                          if(ch=='.')                              MIN=Math.min(MIN,min1+min2-1);                      }                                    }                  System.out.println(MIN);         }}
