import java.io.*;import java.math.*;import java.util.*;import java.util.Arrays;public class Test1{    public static void main(String args[])throws IOException{        Scanner sc=new Scanner(System.in);        int n=sc.nextInt();        int k=sc.nextInt();        int type[]=new int[k];        int i,res=0;        int c=(int)Math.round(n/2.0);        for(i=0;i<n;i++)        {            int temp=sc.nextInt();            type[temp-1]+=1;        }        for(i=0;i<k;i++)        {            if(type[i]>=2)            {                res+=((type[i]/2)*2);                c-=(type[i]/2);                type[i]%=2;            }        }        res+=c;        System.out.println(res);    }    }
