import java.util.*;import java.io.*;/** * main */public class main {public static void main(String[] args) {FastScanner scan=new main().new FastScanner(); long a=scan.nextLong();long b=scan.nextLong();long ans=(long)(-3+Math.sqrt(9+8*(a+b)))/2;System.out.print(a-ans);}    class FastScanner{BufferedReader br=new BufferedReader(new InputStreamReader(System.in));StringTokenizer st=new StringTokenizer("");String next() {while(!st.hasMoreTokens()) {try {st=new StringTokenizer(br.readLine());}catch(IOException e) {e.printStackTrace();}}return st.nextToken();}int nextInt() {return Integer.parseInt(next());}long nextLong() {return Long.parseLong(next());}String nextLine() {String str = "";try {str = br.readLine();} catch (IOException e) {e.printStackTrace();}return str;}}    }
