import java.util.Scanner;public class Main {    public static void main(String args[]) {        Scanner s = new Scanner(System.in);        int n = s.nextInt();        int arr[] = new int[n];        int max = -1,index = -1;        for(int i = 0; i < n; i++){            arr[i] = s.nextInt();            if(arr[i] > max){                max = arr[i];                index = i;            }        }        for(int i = 0; i < index; i++){            if(arr[i] >= arr[i + 1]){                System.out.println("NO");                return;            }        }        for(int i = index + 1; i < n; i++){            if(arr[i] >= arr[i - 1]){                System.out.println("NO");                return;            }        }        System.out.println("YES");    }}
