# code.pyfor _ in range(1):	n,x,y = map(int,input().split())	a = list(map(int,input().split()))	i = 0	index = []	while i<n:		j=i-1		cnt = 0		ok = True		while j>0:			if cnt==x:				ok = True				break			if a[i]>=a[j]:				ok = False				break			else:				cnt+=1 			j-=1		if ok:			j=i+1			cnt = 0			while j<n:				if cnt==y:					ok = True					break				if a[j]<=a[i]:					ok = False					break				else:					cnt+=1				j+=1		if ok:			break		i+=1	print(i+1) 
