//package codeforces;import java.util.*;public class SuperAgent { 	public static void main(String[] args) {		Scanner scan = new Scanner(System.in); 		// 3x3				char[][] A = new char[3][3];		for(int x = 0; x < 3; x++) {			String s = scan.nextLine(); 			for(int y = 0; y < 3; y++) {				A[x][y] = s.charAt(y); 			}		}		// A[0][0] A[0][1] A[0][2]		// A[1][0] A[1][1] A[1][2]		// A[2][0] A[2][1] A[2][2]				// compare		// A[0][0] A[2][2]		String res = "YES";		if (A[0][0] != A[2][2]) {			res = "NO";		}		// A[2][0]  A[0][2]		if (A[2][0] != A[0][2]) {			res = "NO";		}		// A[0][1] A[2][1]		if (A[0][1] != A[2][1]) {			res = "NO";		}		// A[1][0] A[1][2]		if (A[1][0] != A[1][2]) {			res = "NO";		}		System.out.println(res);						//for(char[] a: A) {			//System.out.println(Arrays.toString(a));	//	}			} }
