//package codechef;Â import java.util.*;import java.io.*;public class CodeChef{    public static long arr_max(long arr[])    {        long max = 0;        for(int i = 0; i<arr.length; i++)        {            if(arr[i]>max) max = arr[i];        }        return max;    }    public static int gcd(int a,int b)    {        if(b==0) return a;        return gcd(b,a%b);    }    public static int fact(int n)    {        int fact = 1;        for(int i = 1; i<=n; i++) fact*=i;        return fact;    }    public static boolean isPrime(int n)    {        if(n==2) return true;        else if(n%2==0) return false;        for(int i = 2; i<=Math.sqrt(n); i++)        {            if(n%i==0)            {                return false;            }        }        return true;    }    public static void main(String[] args)throws IOException    {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        int n = Integer.parseInt(br.readLine());        String s = br.readLine();        int arr[] = new int[10];        for(int i = 0; i<n; i++)        {            if(s.charAt(i)=='L')            {                for(int j = 0; j<10; j++)                {                    if(arr[j]==0)                    {                        arr[j]=1;                        break;                    }                }            }            else if(s.charAt(i)=='R')            {                for(int j = 9; j>=0; j--)                {                    if(arr[j]==0)                    {                        arr[j]=1;                        break;                    }                }            }            else            {                arr[Integer.parseInt(s.substring(i,i+1))]=0;            }        }        for(int i = 0; i<10; i++) System.out.print(arr[i]);    }}
