// 11-Feb-2021import java.util.*;import java.io.*; public class B {	static class FastReader { 		BufferedReader br;		StringTokenizer st; 		private FastReader() {			br = new BufferedReader(new InputStreamReader(System.in));		} 		String next() {			while (st == null || !st.hasMoreElements()) {				try {					st = new StringTokenizer(br.readLine());				} catch (IOException e) {					e.printStackTrace();				}			}			return st.nextToken();		} 		int nextInt() {			return Integer.parseInt(next());		} 		int[] nextIntArray(int n) {			int[] a = new int[n];			for (int i = 0; i < n; i++)				a[i] = nextInt();			return a;		} 		int[] nextIntArrayOne(int n) {			int[] a = new int[n + 1];			for (int i = 1; i < n + 1; i++)				a[i] = nextInt();			return a;		} 		long nextLong() {			return Long.parseLong(next());		} 		double nextDouble() {			return Double.parseDouble(next());		} 		String nextLine() {			String str = "";			try {				str = br.readLine();			} catch (IOException e) {				e.printStackTrace();			}			return str;		}	} 	public static void main(String[] args) {		FastReader s = new FastReader();		int t = s.nextInt();		while(t -- > 0) {			int n = s.nextInt(), m = s.nextInt(),k = s.nextInt();			int h[] = s.nextIntArray(n);			boolean f = true;								for(int i = 0; i < n - 1; i++) {								m += h[i] - Math.max(0, h[i + 1] - k);								if(m < 0) {					f = false;					break;				}												}									if(f) {				System.out.println("YES");			}else {				System.out.println("NO");			} 		}							} }
