import java.util.Scanner;public class Main {    public static void main(String args[]) {        Scanner s = new Scanner(System.in);        int n = s.nextInt();        if(n % 2 == 0){            System.out.println("NO");        }        else{            System.out.println("YES");            int arr[] = new int[2 * n];            int k = 1;            for(int i = 0; i < n; i++){                if(i % 2 == 0){                 arr[i] = k;                 arr[i + n] = k + 1;                }                else{                    arr[i] = k + 1;                    arr[i + n] = k;                }                k = k + 2;            }            for(int i = 0; i < 2 * n; i++){                System.out.print(arr[i] + " ");            }        }    }}
