import java.util.*;import java.util.Arrays;public class Main{    static int nthXorFib(int n, int a, int b)      {          if (n == 0)              return a;          if (n == 1)              return b;          if (n == 2)              return (a ^ b);                return nthXorFib(n % 3, a, b);      }      public static void main (String[] args)      {          Scanner sc=new Scanner(System.in);        int t=sc.nextInt();        for(int i=0;i<t;i++)        {        int a=sc.nextInt();        int b = sc.nextInt();        int n = sc.nextInt();                System.out.println(nthXorFib(n, a, b));          }    }  }         
